      super(props)
      this.state = {
        categorie: "home"
      }
      this.goto = this.goto.bind(this);
    }

    updateCategorie(newCategorie){
      this.setState({ categorie: newCategorie })
    }

    goto(e){
        console.log(e.target.value);
        window.scrollTo(0,500);
    }

    render() {
      return(
        <nav className="nav fixed-top">
          <ul className="nav justify-content-end">
            <li className="nav-item" onClick={this.goto}>
              <a className="nav-link" value="home" href="#">Home</a>
            </li>
            <li className="nav-item" onClick={this.goto}>
              <a className="nav-link" value="about" href="#">About</a>
            </li>
            <li className="nav-item" onClick={this.goto}>
              <a className="nav-link" value="skils" href="#">Skils</a>
            </li>
            <li className="nav-item" onClick={this.goto}>
              <a className="nav-link" value="portfolio" href="#">Portfolio</a>
            </li>
          </ul>
      </nav>
            );
    }
}

export default HeaderComponent;
