import React, {Component} from 'react';
import './header.css';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { fab } from '@fortawesome/free-brands-svg-icons'
import { library } from '@fortawesome/fontawesome-svg-core';
library.add(fab);

function vhTOpx(value) {
    var w = window,
      d = document,
      e = d.documentElement,
      g = d.getElementsByTagName('body')[0],
      x = w.innerWidth || e.clientWidth || g.clientWidth,
      y = w.innerHeight|| e.clientHeight|| g.clientHeight;

    var result = (y*value)/100;
    console.log(result);
    return result;
  }

class HeaderComponent extends Component {
    constructor(props) {
      super(props);
      this.state = {
      }; 
    }
    goto(e){
        window.scrollTo(0,vhTOpx(100));
    }
    render() {
      return(
        <div className="header-wrapper">
            <div className="header">
                <ul className="nav justify-content-end navbar-fixed-top">
                    <li className="nav-item">
                        <a className="nav-link" href="#" onClick={this.goto}>Home</a>
                    </li>
                    <li className="nav-item">
                        <a className="nav-link" href="#" onClick={this.goto}>About</a>
                    </li>
                    <li className="nav-item">
                        <a className="nav-link" href="#" onClick={this.goto}>Skils</a>
                    </li>
                    <li className="nav-item">
                        <a className="nav-link" href="#" onClick={this.goto}>Portfolio</a>
                    </li>
                </ul>
            </div>
        </div>
            );
    }
}

export default HeaderComponent;
